package day19_whileLoop_doWhileLoop;

public class C04_DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i= 0;
		
		while (i<5) {
			System.out.print(i+" ");
			i++;
		}
		
		System.out.println();
		
		// do while Loop ile yapýlmasý 
		
		int a=0;
		do {
			System.out.print(a+" ");
			a++;
		}while(a<5);
		
		
		
	}	

}
